// src/App.js
// 메인 앱 컴포넌트: PDF/JPG 업로드, OCR(손글씨/숫자/한글) 인식, 결과 표시
import React, { useState } from 'react';
import PdfUploader from './components/PdfUploader';
import Tesseract from 'tesseract.js';

function App() {
  // 업로드된 파일 상태
  const [uploadedFile, setUploadedFile] = useState(null);
  // OCR 결과 상태
  const [ocrResult, setOcrResult] = useState('');
  // 로딩 상태
  const [isLoading, setIsLoading] = useState(false);

  // OCR 실행 함수
  const handleOcr = async () => {
    if (!uploadedFile) {
      alert('먼저 파일을 업로드해주세요.');
      return;
    }
    setIsLoading(true);
    setOcrResult('');
    try {
      // 이미지 또는 PDF 첫 페이지를 OCR로 인식
      let imageUrl = '';
      if (uploadedFile.type === 'image/jpeg') {
        imageUrl = URL.createObjectURL(uploadedFile);
      } else if (uploadedFile.type === 'application/pdf') {
        // PDF는 미리보기에서 스크린샷을 찍거나, 서버에서 이미지로 변환 필요
        alert('PDF의 경우, 현재는 JPG만 OCR이 지원됩니다.');
        setIsLoading(false);
        return;
      }
      // Tesseract.js로 한글+영어+숫자 인식
      const { data: { text } } = await Tesseract.recognize(
        imageUrl,
        'kor+eng', // 한글+영어+숫자
        {
          logger: m => console.log(m), // 진행상황 콘솔 출력
        }
      );
      setOcrResult(text);
    } catch (error) {
      setOcrResult('OCR 처리 중 오류가 발생했습니다.');
    }
    setIsLoading(false);
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #e3f2fd 0%, #fce4ec 100%)',
      fontFamily: 'Pretendard, Noto Sans KR, sans-serif',
      padding: 32
    }}>
      <div style={{
        maxWidth: 600,
        margin: '0 auto',
        background: '#fff',
        borderRadius: 16,
        boxShadow: '0 4px 24px #e3e3e3',
        padding: 32
      }}>
        <h1 style={{ color: '#1976d2', marginBottom: 24 }}>수학 문제 자동 채점 웹앱</h1>
        <PdfUploader onFileSelect={setUploadedFile} />
        <button
          onClick={handleOcr}
          style={{
            background: '#1976d2', color: '#fff', border: 'none', borderRadius: 8,
            padding: '12px 24px', fontSize: 16, cursor: 'pointer', marginTop: 16
          }}
          disabled={isLoading || !uploadedFile}
        >
          {isLoading ? '인식 중...' : '손글씨/숫자 인식(OCR) 시작'}
        </button>
        {ocrResult && (
          <div style={{ marginTop: 32 }}>
            <h3 style={{ color: '#388e3c' }}>인식 결과</h3>
            <pre style={{
              background: '#f5f5f5', padding: 16, borderRadius: 8,
              whiteSpace: 'pre-wrap', fontSize: 16
            }}>{ocrResult}</pre>
          </div>
        )}
      </div>
      <footer style={{ textAlign: 'center', marginTop: 48, color: '#888' }}>
        © {new Date().getFullYear()} MathOCR. Powered by React & Tesseract.js
      </footer>
    </div>
  );
}

export default App;
